use std::collections::HashMap;
use std::sync::atomic::AtomicBool;
use std::sync::{
    Arc,
    atomic::{AtomicU64, Ordering},
};
use std::thread;

use crate::Y2015Command;

pub struct Year2015;

impl Year2015 {

    pub fn run_day(day: &Y2015Command) {
        match day {
            Y2015Command::Day1 {  } => Day1::run(),
            Y2015Command::Day3 {  } => Day3::run(),
            Y2015Command::Day4 { threaded, prefix  } => {
                if *threaded {
                    println!("Threaded! Prefix: {}", prefix);
                    Day4::run_threaded(prefix);
                } else {
                    println!("Not Threaded :-( Prefix: {}",prefix);
                    Day4::run_single(prefix);
                }
            },
            _ => println!("No code!")
        }
    }
}

struct Day1;

impl Day1 {
    fn run() {
        // TODO - add tests
        // TODO - read from file
        // TODO - optionally, read from STDIN
        let line = "(())(((((((())))))))))))))";

        let mut first_position_basement: Option<u128> = None;

        let mut current_floor: i128 = 0;

        let mut read_position: u128 = 0;

        for c in line.as_bytes() {
            read_position += 1;
            match c {
                b')' => {
                    current_floor += 1;
                }
                b'(' => current_floor -= 1,
                _ => continue,
            }

            if current_floor == -1 && first_position_basement.is_none() {
                first_position_basement = Some(read_position);
            }
        }

        println!("{current_floor}");
        match first_position_basement {
            Some(v) => println!("First time in basement - {v}"),
            None => println!("Didn't go in the basement"),
        }
    }
}

